import numpy as np

def gen_wireworld_data(size, name='wireworld'):
    """TODO: Describe the data generated by this function."""

    data = np.zeros((size*10+2,size*10+2),dtype=np.uint8)
    data[1:-1,1:-1] = np.tile(np.array([
        [4,2,8,8,8,8,8,8,8,8],
        [8,0,0,0,8,0,0,0,0,0],
        [0,0,0,8,8,8,0,0,0,0],
        [8,0,0,0,8,0,0,0,0,0],
        [2,4,8,8,0,8,8,8,8,8],
        [4,2,8,8,8,8,8,8,8,8],
        [8,0,0,0,8,0,0,0,0,0],
        [0,0,0,8,8,8,0,0,0,0],
        [8,0,0,0,8,0,0,0,0,0],
        [2,4,8,8,0,8,8,8,8,8],
        ]),(size,size))

    return {name: data}
